pragma solidity ^0.5.0;

contract Decentragram {
  string public name = "Dappstagram";
  
  //Store Posts
  uint public imageCount = 0;
  mapping(uint => Image) public images;
  
  struct Image {
    uint id;
    string hash;
    string description;
    uint tipAmount;
    address payable author;
  }

  event ImageUploaded(
    uint id,
    string hash,
    string description,
    uint tipAmount,
    address payable author
  );
  event ImageTipped(
    uint id,
    string hash,
    string description,
    uint tipAmount,
    address payable author
  );

  //Create Posts
  function uploadImage(string memory _imgHash, string memory _description) public {
    imageCount++;
    require(bytes(_imgHash).length > 0, "No Image Uploaded");
    require(bytes(_description).length > 0, "No Description Uploaded");
    require(msg.sender != address(0x0));
    images[imageCount] = Image(imageCount, _imgHash, _description, 0, msg.sender);
    emit ImageUploaded(imageCount, _imgHash, _description, 0, msg.sender);
  }

  //Tip Posts
  function tipImageOwner(uint _id) public payable {
    require(_id > 0 && _id <= imageCount); //images are 1 indexed
    Image memory _image = images[_id];
    address payable _author = _image.author;
    _author.transfer(msg.value);
    _image.tipAmount = _image.tipAmount + msg.value;
    images[_id] = _image;
    emit ImageTipped(_id, _image.hash, _image.description, _image.tipAmount, _image.author);
  }
}
